import React, { useMemo, useState } from 'react'
import { NextPage } from 'next'
import Layout from '@components/layout/Layout'
import routes from '@utils/routes'
import Link from 'next/link'
import { FiArrowRight } from 'react-icons/fi'
import TableComponent from '@components/Table'
import makeData from '@utils/helpers'

const headerSearch = () => {
  return (
    <div className="flex justify-start" style={{ width: '55%' }}>
      <p>an header search here</p>
    </div>
  )
}

const IndexPage: NextPage = () => {
  const [data, setData] = useState(() => makeData(20))
  const columns = useMemo(
    () => [
      {
        Header: 'Table',
        columns: [
          {
            Header: 'First Name',
            accessor: 'firstName',
          },
          {
            Header: 'Last Name',
            accessor: 'lastName',
          },
          {
            Header: 'Email',
            accessor: 'email',
          },
        ],
      },
      // {
      //   Header: 'Test Info',
      //   columns: [
      //     {
      //       Header: 'Invite Sent',
      //       accessor: 'inviteSent',
      //     },
      //     {
      //       Header: 'Test Sent',
      //       accessor: 'testSent',
      //     },
      //     {
      //       Header: 'Test Status',
      //       accessor: 'testStatus',
      //     },
      //   ],
      // },
    ],
    []
  )
  return (
    <Layout
      title="Your Awesome App"
      className="container py-8 overflow-auto"
      header={headerSearch()}
    >
      {/* <h1 className="mb-4 text-3xl font-black dark:text-gray-100">Welcome</h1> */}
      <p className="mb-4 text-lg font-light">
        Demo spreadsheet upload and email list system.
      </p>
      <div>
        <TableComponent
          columns={columns}
          data={data}
          setData={setData}
          // editableFields={['firstName', 'lastName', 'email']}
        />
      </div>
    </Layout>
  )
}

export default IndexPage
