import React, { useEffect, useState } from 'react'
import clsx from 'clsx'
import {
  useTable,
  usePagination,
  useRowSelect,
  useSortBy,
  useFilters,
  useColumnOrder,
  useGlobalFilter,
  useAsyncDebounce,
} from 'react-table'
import { motion, AnimatePresence } from 'framer-motion'
import matchSorter from 'match-sorter'

import styled from 'styled-components'
import Input from './Input'
import Checkbox from './Checkbox'

export interface Props {
  className?: string | Record<string, unknown>
  ordered?: boolean
  item?: boolean
}

export interface gFilterProps {
  preGlobalFilteredRows: unknown
  globalFilter: unknown
  setGlobalFilter: unknown
}

export type gFilterState = unknown

export type filterColumnProps = unknown

export type FilterType = {
  fuzzyText: unknown
  text: (rows: unknown, id: unknown, filterValue: unknown) => unknown
}

export type DefaultColumn = {
  Filter?: ({
    column: { filterValue, preFilteredRows, setFilter },
  }: {
    column: {
      filterValue: unknown
      preFilteredRows: unknown
      setFilter: unknown
    }
  }) => JSX.Element
  Cell?: ({
    value: initialValue,
    row: { index },
    column,
    updateData,
  }: {
    value: unknown
    row: {
      index: unknown
    }
    column: unknown
    updateData: (index, id, value) => unknown
  }) => unknown
}

export type GlobalFilter = {
  use: boolean
  component: React.Component<gFilterProps, gFilterState>
}

export interface TableOptions {
  filterTypes?: FilterType
  defaultColumn?: DefaultColumn
  globalFilter?: GlobalFilter
  renderHeader?: (header: unknown) => JSX.Element
  renderFilter?: (column: unknown) => JSX.Element
  renderTableBody?: (column: unknown) => JSX.Element
  renderTableRow?: (row: unknown) => JSX.Element
  renderTableCell?: (cell: unknown) => JSX.Element
  customHook?: (hooks: unknown) => unknown
}

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 0;
      }
    }

    .pagination {
      padding: 0.5rem;
    }
  }
`

function TableComponent({
  columns,
  data,
  setData,
  ...rest
}: {
  columns: {
    Header?: string
    columns: {
      Header: string
      accessor: string
      Cell?: unknown
    }[]
  }[]
  data: Record<string, unknown>
  setData: React.Dispatch<unknown>
} & TableOptions) {
  const [skipPageReset, setSkipPageReset] = useState(false)

  // We need to keep the table from resetting the pageIndex when we
  // Update data. So we can keep track of that flag with a ref.

  // When our cell renderer calls updateMyData, we'll use
  // the rowIndex, columnId and new value to update the
  // original data
  const updateData = (rowIndex, columnId, value) => {
    // We also turn on the flag to not reset the page
    setSkipPageReset(true)
    setData((old) =>
      old.map((row, index) => {
        if (index === rowIndex) {
          return {
            ...old[rowIndex],
            [columnId]: value,
          }
        }
        return row
      })
    )
  }

  // After data chagnes, we turn the flag back off
  // so that if data actually changes when we're not
  // editing it, the page is reset
  useEffect(() => {
    setSkipPageReset(false)
  }, [data])

  const tableOptions: TableOptions = {}

  Array.from([
    'renderHeader',
    'renderFilter',
    'renderTableBody',
    'renderTableRow',
    'renderTableCell',
    'customHook',
  ]).forEach((option) => {
    if (rest[option]) {
      tableOptions[option] = rest[option]
    }
  })

  return (
    <Styles>
      <Table
        columns={columns}
        data={data}
        updateData={updateData}
        skipPageReset={skipPageReset}
        options={{ ...tableOptions }}
      />
    </Styles>
  )
}

export default TableComponent

// Be sure to pass our updateData and the skipPageReset option
function Table({ columns, data, updateData, skipPageReset, options }) {
  const {
    filterTypes,
    defaultColumn,
    globalFilter,
    renderHeader,
    renderFilter,
    renderTableBody,
    renderTableRow,
    renderTableCell,
    customHook,
  }: TableOptions = options || {}

  let extraHook = (hooks) => {
    hooks.visibleColumns.push((columns) => [...columns])
  }
  if (customHook) extraHook = customHook

  const hookOptions: {
    filterTypes?: FilterType
    defaultColumn?: DefaultColumn
  } = {}

  if (filterTypes) hookOptions.filterTypes = filterTypes
  if (defaultColumn) hookOptions.defaultColumn = defaultColumn
  else hookOptions.defaultColumn = {}

  console.log('hook options', options)

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    canPreviousPage,
    canNextPage,
    pageOptions,
    pageCount,
    gotoPage,
    nextPage,
    previousPage,
    setGlobalFilter,
    setGlobalFilteredRows,
    setPageSize,
    selectedFlatRows,
    visibleColumns,
    preGlobalFilteredRows,
    // setColumnOrder,
    state,
  } = useTable(
    {
      columns,
      data,
      // use the skipPageReset option to disable page resetting temporarily
      autoResetPage: !skipPageReset,
      updateData,
      ...hookOptions,
    },
    useColumnOrder,
    useFilters,
    useGlobalFilter,
    useSortBy,
    usePagination,
    useRowSelect,
    extraHook
  )

  const { pageIndex, pageSize, selectedRowIds } = state

  const spring = React.useMemo(
    () => ({
      type: 'spring',
      damping: 50,
      stiffness: 100,
    }),
    []
  )

  // Render the UI for your table
  return (
    <>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <motion.th
                  {...column.getHeaderProps({
                    layoutTransition: spring,
                    style: {},
                  })}
                >
                  <div {...column.getSortByToggleProps()}>
                    {renderHeader ? (
                      renderHeader(column)
                    ) : (
                      <>
                        {column.render('Header')}
                        <span>
                          {column.isSorted
                            ? column.isSortedDesc
                              ? ' ðŸ”½'
                              : ' ðŸ”¼'
                            : ''}
                        </span>
                      </>
                    )}
                  </div>
                  <div>
                    {column.canFilter && filterTypes
                      ? renderFilter
                        ? renderFilter(column)
                        : column.render('Filter')
                      : null}
                  </div>
                </motion.th>
              ))}
            </tr>
          ))}
          {globalFilter?.use && (
            <tr>
              <th
                colSpan={visibleColumns.length}
                style={{
                  textAlign: 'left',
                }}
              >
                <globalFilter.component
                  preGlobalFilteredRows={preGlobalFilteredRows}
                  globalFilter={state.globalFilter}
                  setGlobalFilter={setGlobalFilter}
                />
              </th>
            </tr>
          )}
        </thead>
        <tbody {...getTableBodyProps()}>
          <AnimatePresence>
            {page.map((row, i) => {
              prepareRow(row)
              return renderTableRow ? (
                renderTableRow(row)
              ) : (
                <motion.tr
                  {...row.getRowProps({
                    layoutTransition: spring,
                    exit: { opacity: 0, maxHeight: 0 },
                  })}
                >
                  {row.cells.map((cell, i) => {
                    return (
                      <motion.td
                        {...cell.getCellProps({
                          layoutTransition: spring,
                        })}
                      >
                        {renderTableBody
                          ? renderTableCell(cell)
                          : cell.render('Cell')}
                      </motion.td>
                    )
                  })}
                </motion.tr>
              )
            })}
          </AnimatePresence>
        </tbody>
      </table>
      <div className="pagination">
        <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
          {'<<'}
        </button>{' '}
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>
          {'<'}
        </button>{' '}
        <button onClick={() => nextPage()} disabled={!canNextPage}>
          {'>'}
        </button>{' '}
        <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
          {'>>'}
        </button>{' '}
        <span>
          Page{' '}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>{' '}
        </span>
        <span>
          | Go to page:{' '}
          <input
            type="number"
            defaultValue={pageIndex + 1}
            onChange={(e) => {
              const page = e.target.value ? Number(e.target.value) - 1 : 0
              gotoPage(page)
            }}
            style={{
              width: '100px',
            }}
          />
        </span>{' '}
        <select
          value={pageSize}
          onChange={(e) => {
            setPageSize(Number(e.target.value))
          }}
        >
          {[10, 20, 30, 40, 50].map((pageSize) => (
            <option key={pageSize} value={pageSize}>
              Show {pageSize}
            </option>
          ))}
        </select>
      </div>
      <pre>
        <code>
          {JSON.stringify(
            {
              state,
              selectedRowIds: selectedRowIds,
              'selectedFlatRows[].original': selectedFlatRows.map(
                (d) => d.original
              ),
            },
            null,
            2
          )}
        </code>
      </pre>
    </>
  )
}
