import React, { useMemo, useState } from 'react'
import { NextPage } from 'next'
import Layout from '@components/layout/Layout'
import routes from '@utils/routes'
import Link from 'next/link'
import { FiArrowRight } from 'react-icons/fi'
import TableComponent from '@components/Table'
import makeData from '@utils/helpers'

const headerSearch = () => {
  return (
    <div className="flex justify-start" style={{ width: '55%' }}>
      <p>an header search here</p>
    </div>
  )
}

const columnsd =
  (() => [
    {
      Header: 'Name',
      columns: [
        {
          Header: 'First Name',
          accessor: 'firstName',
        },
        {
          Header: 'Last Name',
          accessor: 'lastName',
        },
        {
          Header: 'Email',
          accessor: 'email',
        },
      ],
    },
    {
      Header: 'Info',
      columns: [
        {
          Header: 'Age',
          accessor: 'age',
        },
        {
          Header: 'Visits',
          accessor: 'visits',
          Cell: (row) => 'disableEdit',
        },
        {
          Header: 'Status',
          accessor: 'status',
          Cell: (row) => 'disableEdit',
        },
        {
          Header: 'Profile Progress',
          accessor: 'progress',
          Cell: (row) => 'disableEdit',
        },
      ],
    },
  ],
  [])

const IndexPage: NextPage = () => {
  const [data, setData] = useState(() => makeData(20))
  const columns = useMemo(
    () => [
      {
        Header: 'Table',
        columns: [
          {
            Header: 'First Name',
            accessor: 'firstName',
          },
          {
            Header: 'Last Name',
            accessor: 'lastName',
          },
        ],
      },
      {
        Header: 'Info',
        columns: [
          {
            Header: 'Age',
            accessor: 'age',
          },
          {
            Header: 'Visits',
            accessor: 'visits',
          },
          {
            Header: 'Status',
            accessor: 'status',
          },
          {
            Header: 'Profile Progress',
            accessor: 'progress',
          },
        ],
      },
    ],
    []
  )
  return (
    <Layout
      title="Your Awesome App"
      className="container max-w-xl py-8"
      header={headerSearch()}
    >
      <h1 className="mb-4 text-3xl font-black dark:text-gray-100">Welcome</h1>
      <p className="mb-4 text-lg font-light">
        This is an template project using NextJS with TailwindCSS including dark
        mode switcher.
      </p>
      <div>
        <TableComponent columns={columns} data={data} setData={setData} />
      </div>
      <p className="mb-6 text-lg font-light">
        It includes some example components with jest testing and typography but
        it's not complete nor is it tested well.
      </p>
      <div className="space-x-4">
        <Link {...routes.COMPONENTS}>
          <a
            className="px-4 py-2 text-white bg-primary hover:bg-primary-darker"
            title="components"
          >
            Components <FiArrowRight className="inline ml-1" />
          </a>
        </Link>
        <Link {...routes.TYPOGRAPHY}>
          <a
            className="px-4 py-2 text-white bg-secondary hover:bg-secondary-darker"
            title="Typography"
          >
            Typography <FiArrowRight className="inline ml-1" />
          </a>
        </Link>
      </div>
    </Layout>
  )
}

export default IndexPage
